interface KlayOracleInterface {
    struct Request {
        bytes32 requestId;
        address nodeAddress;
        bytes32 adapterId;
        bytes4 callbackFunctionId;
        address callBackContract;
        bytes32 data;
        uint256 timestamp;
    }

